import React from 'react';
import styles from './Statistics.module.css'


//кнопки статистики.
//options - это коллбек методов класса секции
const FeedbackOptions = ({options}) =>{
return(
    <div className={styles.FeedbackOptions}>
        <div className={styles.feedbackBtns}>
            <button className={styles.btn} onClick={options.leaveGood}>Good</button>
            <button className={styles.btn} onClick={options.leaveNeutral}>Neutral</button>
            <button className={styles.btn} onClick={options.leaveBad}>Bad</button>
        </div>
    </div>
)
}
//


//принимает данные статистики и выводит их 
const Statistics = ({good,neutral,bad,total,positiveDomination}) =>{
    return(
    <div className={styles.Statistics}>
            <span className={styles.stat}>Good {good}</span>
            <span className={styles.stat}>Neutral {neutral}</span>
            <span className={styles.stat}>Bad {bad}</span>
            <span className={styles.stat}>Total {total}</span>
            <span className={styles.stat}>
            Positive feedback {positiveDomination}%
            </span>
        </div>
        )
}
//


//классовый компонент секции. он тут всем заправляет
class Section extends React.Component{
    state = {
        good: 0,
        neutral: 0,
        bad: 0
    }
    //сумму и процент позитивных никуда не записываю, просто возвращаю из коллбек функций


    //коллбеки. ничего особенного.
    leaveGood = ()=>this.setState(state=>({good:state.good+1}));

    leaveBad = () =>this.setState(state=>({bad: state.bad+1}));

    leaveNeutral = ()=> this.setState(state=>({neutral: state.neutral+1}));
    //

    //сумма. юзается уже в рендере и в подсчете позитивщиков
    countTotal = () => this.state.good+this.state.neutral+this.state.bad;
    //

    // подсчет людей, которые нажали не ту кнопку
    howManyPositive =()=> Math.round(100/this.countTotal()*(this.state.good));
    //


    render(){
        return(
        <div className={styles.Section}>
            <span className={styles.title}>{this.props.title}</span>

        {/* не понимаю к чему в задании был onLeaveFeedback помимо options */}
            <FeedbackOptions 
            options={{
            leaveGood: this.leaveGood,
            leaveNeutral: this.leaveNeutral,
            leaveBad:this.leaveBad
            }}/>


            {/* проверка на статистику */}
            {this.countTotal()>=1 
            ? <Statistics 
            good={this.state.good}
            neutral={this.state.neutral} 
            bad={this.state.bad} 
            total={this.countTotal()} 
            positiveDomination={this.howManyPositive()}/>
            : <span className='noFeedback'>no feedback given. u can be first</span>
            }
        </div>
        )
    }
}


export default Section;
