import React from 'react';
//
const Statistics = ({good,neutral,bad,total,positiveDomination}) =>{
    return(
    <div className='Statistics'>
        <span className="good">Good {good}</span>
            <span className="neutral">Neutral {neutral}</span>
            <span className="bad">Bad {bad}</span>
            <span className="total">Total {total}</span>
            <span className='positiveDomination'>Positive feedback        {positiveDomination}%</span>
        </div>
        )
}
//
const FeedbackOptions = ({options}) =>{
    console.log('options :>> ', options);
return(
    <div className='FeedbackOption'>
        <span className='title'>Plz leave feedback</span>
        <div className='feedback-btns'>
            <button className='btn' onClick={options.leaveGood}>Good</button>
            <button className='btn' onClick={options.leaveNeutral}>Neutral</button>
            <button className='btn' onClick={options.leaveBad}>Bad</button>
        </div>
    </div>
)
}

class Section extends React.Component{
    state = {
        good: 0,
        neutral: 0,
        bad: 0
    }
    counts = {
        total: 0,
        positiveDomination: 0,
    }

    leaveGood = ()=>{
        this.setState(state => ({good: state.good+1}));
        this.setState(counts=>({total:counts.total++}))
        this.howManyPositive(true);
    }
    leaveBad = () =>{
        this.setState(state=>({bad: state.bad++}));
        this.setState(counts=>({total:counts.total++}))
        // this.counts.total++;    
        this.howManyPositive();
    }
    leaveNeutral = ()=>{
        this.setState(state=>({neutral: state.neutral+1}));
        this.setState(counts=>({total:counts.total+1}))
        this.howManyPositive();
    }
    //Работает через сраку, но я старался. честно.
    howManyPositive =(isGood)=>{
        console.log(this.counts.total);
        if(isGood===true){
            console.log('this.good :>> ', this.state.good);
            console.log('this.total :>> ', this.total);
            this.counts.positiveDomination = Math.round(100/(this.counts.total+1)*(this.state.good+1));
            //из-за асинхронности пришлось добавлять +1.
            // если вызвать консол лог, то state.good будет на 1 меньше
        }else{
            // console.log('isGood :>> ', isGood);
            this.counts.positiveDomination = Math.round(100/(this.counts.total+1)*(this.state.good));
        }
    }

    render(){
        return(
        <div className='Section'>
            <FeedbackOptions options={
            {leaveGood: this.leaveGood,
            leaveNeutral: this.leaveNeutral,
            leaveBad:this.leaveBad
            }
            }/>

            <Statistics good={this.state.good}
            neutral={this.state.neutral} 
            bad={this.state.bad} 
            total={this.counts.total} 
            positiveDomination={this.counts.positiveDomination}/>
        </div>

        )
    }
}


export default Section;
