import React from 'react';

const Statistics = ({good,neutral,bad,total})


class FeedbackOptions extends React.Component{
    state = {
        good: 0,
        neutral: 0,
        bad: 0
    }

    total = 0;
    positiveDomination= 0;

    leaveGood = ()=>{
        this.setState(state => ({good: state.good+1}));
        this.total++;
        this.howManyPositive(true);
    }
    leaveBad = () =>{
        this.setState(state=>({bad: state.bad+1}));
        this.total++;    
        this.howManyPositive();
    }
    leaveNeutral = ()=>{
        this.setState(state=>({neutral: state.neutral+1}));
        this.total++;
        this.howManyPositive();
    }
    //Работает через сраку, но я старался. честно.
    howManyPositive =(isGood)=>{
        if(isGood===true){
            this.positiveDomination = Math.round(100/this.total*(this.state.good+1));
            //из-за асинхронности пришлось добавлять +1.
            // если вызвать консол лог, то state.good будет на 1 меньше
        }else{
            console.log('isGood :>> ', isGood);
            this.positiveDomination = Math.round(100/this.total*(this.state.good));
        }
    }


    render(){
        return(
        <div className='Statistics'>
            <div className='Statistics-leaver'>
                <span className='title'>Plz leave feedback</span>
                <div className='feedback-btns'>
                    <button className='btn' onClick={this.leaveGood}>Good</button>
                    <button className='btn' onClick={this.leaveNeutral}>Neutral</button>
                    <button className='btn' onClick={this.leaveBad}>Bad</button>
                </div>
            </div>
            <div className='Statistics-left'>
                <span className="good">Good {this.state.good}</span>
                <span className="neutral">Neutral {this.state.neutral}</span>
                <span className="bad">Bad {this.state.bad}</span>
                <span className="total">Total {this.total}</span>
                <span className='positiveDomination'>Positive feedback {this.positiveDomination}%</span>
            </div>
        </div>

        )
    }
}


export default FeedbackOptions;
