import React from 'react';
//принимает данные статистики и выводит их 
const Statistics = ({good,neutral,bad,total,positiveDomination}) =>{
    return(
    <div className='Statistics'>
        <span className="good">Good {good}</span>
            <span className="neutral">Neutral {neutral}</span>
            <span className="bad">Bad {bad}</span>
            <span className="total">Total {total}</span>
            <span className='positiveDomination'>Positive feedback        {positiveDomination}%</span>
        </div>
        )
}
//

//кнопки статистики.
//options - это коллбек функции секции
const FeedbackOptions = ({options}) =>{
return(
    <div className='FeedbackOption'>
        <span className='title'>Plz leave feedback</span>
        <div className='feedback-btns'>
            <button className='btn' onClick={options.leaveGood}>Good</button>
            <button className='btn' onClick={options.leaveNeutral}>Neutral</button>
            <button className='btn' onClick={options.leaveBad}>Bad</button>
        </div>
    </div>
)
}
//

//классовый компонент секции. он тут всем заправляет
class Section extends React.Component{
    state = {
        good: 0,
        neutral: 0,
        bad: 0
    }
    //сумму и процент позитивных никуда не записываю, просто возвращаю из коллбек функций


    //коллбеки. ничего особенного вызывается коллбек подсчета процентов
    leaveGood = ()=>{
        this.setState(state=>({good:state.good+1}),this.howManyPositive);
        console.log('this.state :>> ', this.state);
    }
    leaveBad = () =>{
        this.setState(state=>({bad: state.bad+1}), this.howManyPositive);
    }
    leaveNeutral = ()=>{
        this.setState(state=>({neutral: state.neutral+1}), this.howManyPositive);
    }
    //
    countTotal = () =>{
        return this.state.good+this.state.neutral+this.state.bad;
    }
    howManyPositive =()=>{
        let total = this.countTotal();
        console.log('total :>> ', this.countTotal());     
        return Math.round(100/total*(this.state.good));
    }

    render(){
        console.log('countTotal :>> ', this.countTotal);
        return(
        <div className='Section'>
            <FeedbackOptions options={
            {leaveGood: this.leaveGood,
            leaveNeutral: this.leaveNeutral,
            leaveBad:this.leaveBad
            }
            }/>

            <Statistics good={this.state.good}
            neutral={this.state.neutral} 
            bad={this.state.bad} 
            total={this.countTotal()} 
            positiveDomination={this.howManyPositive()}/>
        </div>
        )
    }
}


export default Section;
